    ArrayList<CategoryModel> categoryModelArrayList;

    private ArrayList<CategoryModel> prepareDataCategoryData() {

        ArrayList<CategoryModel> android_version = new ArrayList<>();
        for (int i = 0; i < category_names.length; i++) {

            CategoryModel CategoryModel = new CategoryModel();
            CategoryModel.setCategory_name(category_names[i]);
            CategoryModel.setCategory_image(category_image_urls[i]);
            android_version.add(CategoryModel);
        }
        return android_version;
    }
