package gogagner.goldenbrainsithub.commanview.fragment;

import android.app.Fragment;
import android.os.Bundle;
import android.support.v7.widget.LinearLayoutManager;
import android.support.v7.widget.RecyclerView;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;
import android.widget.TextView;

import java.util.ArrayList;
import java.util.Timer;
import java.util.TimerTask;

import gogagner.goldenbrainsithub.adapter.AdViewAdapter;
import gogagner.goldenbrainsithub.adapter.CategoryAdapter;
import gogagner.goldenbrainsithub.com.R;
import gogagner.goldenbrainsithub.model.AddModel;
import gogagner.goldenbrainsithub.model.CategoryModel;

public class AddFragment extends Fragment {

    private final String android_version_names[] = {
            "Donut",
            "Eclair",
            "Froyo",
            "Gingerbread",
            "Honeycomb",
            "Ice Cream Sandwich",
            "Jelly Bean"
            /*"KitKat",
            "Lollipop",
            "Marshmallow"*/
    };
    private final String android_image_urls[] = {
            "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTiwKdyCn5wz0PtP0rEclTtBauvnKyNcXLfGBfpmAadlRPdklolIw",
            "http://www.indiantelevision.com/sites/default/files/styles/smartcrop_800x800/public/images/tv-images/2018/06/25/Kotak_Mahindra.jpg?itok=i7bB4hMB",
            "data:image/png;base64,",
            "data:image/jpeg;base64,",
            "https://assets.tatacliq.com/medias/sys_master/images/13473103118366.jpg",
            "data:image/jpeg;base64,",
            "data:image/jpeg;base64,"
            /*"http://api.learn2crack.com/android/images/kitkat.png",
            "http://api.learn2crack.com/android/images/lollipop.png",
            "http://api.learn2crack.com/android/images/marshmallow.png"*/
    };

    public AddFragment() {
    }

    RecyclerView rv;
    public int position = 0;
    Timer timer;
    ArrayList<AddModel> addModelArrayList;

    @Override
    public View onCreateView(LayoutInflater inflater, ViewGroup container, Bundle savedInstanceState) {

        View rootView = inflater.inflate(R.layout.layout_add_fragement, container, false);
         rv = (RecyclerView) rootView.findViewById(R.id.recycler_add_fragment);

         addModelArrayList = new ArrayList<AddModel>();
        rv.setLayoutManager(new LinearLayoutManager(getActivity(), 0, false));
        addModelArrayList = prepareData();
        final AdViewAdapter adapter = new AdViewAdapter(getActivity(), addModelArrayList);
        rv.setAdapter(adapter);

        timer = new Timer();
        timer.scheduleAtFixedRate(new RemindTask(), 0, 2000);

        return rootView;


    }

    private class RemindTask extends TimerTask {
        @Override
        public void run() {
            if(getActivity() == null)
                return;
            getActivity().runOnUiThread(new Runnable() {
                @Override
                public void run() {
                    try
                    {
                        if(addModelArrayList!=null ){
                            if (position == addModelArrayList.size()) {
                                position = 0;
                            } else {
                                position++;
                            }
                            rv.smoothScrollToPosition(position);
                        }
                    }
                    catch (Exception e){

                    }
                }
            });
        }
    }


    private ArrayList<AddModel> prepareData() {

        ArrayList<AddModel> android_version = new ArrayList<>();
        for (int i = 0; i < android_version_names.length; i++) {
            AddModel addModel = new AddModel();
            addModel.setAdd_name(android_version_names[i]);
            addModel.setAdd_image(android_image_urls[i]);
            android_version.add(addModel);
        }
        return android_version;
    }
}
